// API Configuration
export const API_URL = 'https://smart-farmer-api.example.com/api/v1';
export const SOCKET_URL = 'https://smart-farmer-api.example.com';

// App Constants
export const APP_NAME = 'Smart Farmer';
export const APP_VERSION = '1.0.0';

// Storage Keys
export const STORAGE_KEYS = {
  AUTH_TOKEN: '@auth_token',
  REFRESH_TOKEN: '@refresh_token',
  USER_DATA: '@user_data',
  LANGUAGE: '@language',
  THEME: '@theme',
  ONBOARDING_COMPLETED: '@onboarding_completed',
  LAST_SYNC: '@last_sync',
};

// App Limits
export const LIMITS = {
  MAX_IMAGE_SIZE: 10 * 1024 * 1024, // 10MB
  MAX_VIDEO_LENGTH: 60, // seconds
  MAX_GROUP_MEMBERS: 100,
  MAX_MESSAGE_LENGTH: 1000,
};

// Features flags
export const FEATURES = {
  DISEASE_DETECTION: true,
  GROUP_CHAT: true,
  OFFLINE_MODE: true,
  ADVISORY_SERVICE: true,
  WEATHER_ALERTS: true,
  MARKET_PRICES: false, // Coming soon
  VOICE_ASSISTANT: false, // Coming soon
};

// Error messages
export const ERROR_MESSAGES = {
  NETWORK_ERROR: 'Network error. Please check your connection.',
  SERVER_ERROR: 'Server error. Please try again later.',
  AUTH_EXPIRED: 'Your session has expired. Please log in again.',
  INVALID_CREDENTIALS: 'Invalid email or password.',
  REQUIRED_FIELD: 'This field is required.',
  INVALID_EMAIL: 'Please enter a valid email address.',
  PASSWORD_WEAK: 'Password must be at least 8 characters with numbers and letters.',
  PASSWORDS_DONT_MATCH: 'Passwords do not match.',
  PHONE_INVALID: 'Please enter a valid phone number.',
};

// Timeouts
export const TIMEOUTS = {
  SYNC_INTERVAL: 5 * 60 * 1000, // 5 minutes
  SESSION_TIMEOUT: 24 * 60 * 60 * 1000, // 24 hours
  CACHE_EXPIRY: 7 * 24 * 60 * 60 * 1000, // 7 days
};
